import "./App.css";
import homelogo from './components/images/water.jpg';
import About from "./components/About";
import Activities from "./components/Activities";
import { useState,useEffect } from "react";
function App() {
    let animate=get
    const [themeicon,setthemeimage]=useState(<i class="fa-solid fa-moon"></i>);
    const [theme,setTheme]=useState("lightmode");
  function changetheme(btn){
    if(theme==="darkmode"){
      setTheme("lightmode");
      btn.target.style.transform="translateX(10px)";
      setthemeimage(<i class="fa-solid fa-moon"></i>);
    }
    else{
      setTheme("darkmode");
      btn.target.style.transform="translateX(0px)";
      setthemeimage(<i class="fa-regular fa-sun"></i>);
    }
  }
//   useEffect(()=>{
//     document.body.className=theme;
//   },[theme])

  return (
    <div>
      <div className="header">
            <a className="logo" href="index.html"> </a>
            <div className="lnk">
                <a className="ig home" href="#home">Home</a>
                <a className="ig services" href="#service">Activities</a>
                <a className="ig teams" href="#tem">Our Team</a>
                <button onClick={changetheme} className="togglebtn"><div id="animate">{themeicon} </div></button>
            </div>
        </div>
        <div id="Home">
            <div className="info">
                <h1 id="astitva">सह अस्तित्व</h1>
                <h2>Existence with coexistence</h2>
                <div className="subtitle">
                    Our Mission is to make every human being able live in harmony in all four levels

                </div>
                <button>Participate in श्रमदान</button>
            </div>
            <div className="slider">
                <img src={homelogo} alt="water"/>
            </div>
        </div>
        <About></About>
        <Activities></Activities>
    </div>
  );
}

export default App;
